class PlayerController extends MonoBehaviour {

  @SerializeField
  playerNumber;

  moveSpeed = 5;

  up;
  down;
  right;
  left;
  bomb;

  playerLooking;

  canMove = true;

  //Start is called before the first frame update
  Start() {
    if(this.playerNumber == 1){
      this.up = KeyCode.W;
      this.down = KeyCode.S;
      this.right = KeyCode.D;
      this.left = KeyCode.A;
      this.bomb = KeyCode.Space;
    }else{
      this.up = KeyCode.UpArrow;
      this.down = KeyCode.DownArrow;
      this.right = KeyCode.RightArrow;
      this.left = KeyCode.LeftArrow;
      this.bomb = KeyCode.Keypad0;
    }


    this.playerLooking = new Vector3(0,0,1);
  }

  //Update is called once per frame
  Update() {
    this.transform.localEulerAngles = new Vector3(0,0,0)
    // Movement
    var newLooking = this.playerLooking;

    if(Input.GetKey(this.right)){
      newLooking = new Vector3(1,0,0);
      this.Move(newLooking);
    }
    if(Input.GetKey(this.left)){
      newLooking = new Vector3(-1,0,0);
      this.Move(newLooking);
    }
    if(Input.GetKey(this.up)){
      newLooking = new Vector3(0,0,1);
      this.Move(newLooking);
    }
    if(Input.GetKey(this.down)){
      newLooking = new Vector3(0,0,-1);
      this.Move(newLooking);
    }

    // Bombs
    if(Input.GetKeyDown(this.bomb)){
      var bomb = Instantiate("Bomb");
      bomb.transform.position = this.transform.position;
    }

    // if(pos != this.transform.position){
    //   this.transform.position = pos;
    // }
  }

  Move(look){
    this.playerLooking = look;

    var pos = this.transform.position;
    var face = this.transform.parent.Find("Face");

    // face.transform.position = new Vector3(pos.x + (this.playerLooking.x  * 0.7),
    //                                       pos.y, 
    //                                       pos.z + (this.playerLooking.z  * 0.7));

    this.transform.GetComponent(Rigidbody).velocity = new Vector3(this.playerLooking.x * this.moveSpeed,
                                          0,
                                          this.playerLooking.z * this.moveSpeed);

  }

}

