class MapGenerator extends MonoBehaviour {

  mapMatrix;
  width;
  height;

  playerArea = 4;
  wallLayers = 1;

  //Start is called before the first frame update
  Start() {

    var playerNumber = 1;

    this.height = Math.floor(this.transform.localScale.z);
    this.width = Math.floor(this.transform.localScale.x);
    for(var i = -this.wallLayers ; i <= this.width + this.wallLayers; i++){
      for(var j = -this.wallLayers ; j <= this.height + this.wallLayers; j++){

        var p1Pos = (i == 0 && j == 0);
        var p2Pos = (i == this.width-1 && j == this.height-1);

        if(!p1Pos && !p2Pos){
          var p1Area = (i < this.playerArea && j < this.playerArea)
          var p2Area = (i > this.width-1 - this.playerArea && j > this.height-1 - this.playerArea)

          var walls = i < 0 || j < 0 || i >= this.width || j >= this.height;
          if(walls){
            this.AddWall(i,j);
          }else if (!p1Area && !p2Area && Random.Range(0,3,true) != 0){
            this.AddBlock(i,j);
          }
        }else{
          this.AddPlayer(i,j,playerNumber);
          playerNumber++;
        }
      }
    }
  }

  //Update is called once per frame
  Update() {

  }

  AddBlock(i,j){
    var block = Instantiate("Block");
    this.PlaceOnTopOfFloor(i,j,block);
  }

  AddWall(i,j){
    var wall = Instantiate("Wall");
    this.PlaceOnTopOfFloor(i,j,wall);
  }

  AddPlayer(i,j,pNum){
    var name = (pNum == 1)? "PlayerOne" : "PlayerTwo";
    var player = Instantiate(name);
    player = this.PlaceOnTopOfFloor(i + 0.5, j + 0.5, player);
  }

  PlaceOnTopOfFloor(i,j,obj){
    var pos = this.transform.position;
    var xx = pos.x - (this.width/2) + i + (obj.transform.localScale.x/2);
    var yy = pos.y + (obj.transform.localScale.y/2) + (this.transform.localScale.y/2);
    var zz =  pos.z - (this.height/2) + j + (obj.transform.localScale.z/2);
    obj.transform.position = new Vector3(xx, yy, zz);

    return obj;
  }
}

